import React from 'react';
import { createContext, useState } from 'react';

//create context with hook
export const CurrentUserContext = createContext([{}, () => {}]);

//create Provider gives access to the all components in "children"
export const CurrentUserProvider = ({ children }) => {
   const [state, setState] = useState({
      isLoading: false, // user is loaded?
      isLoggedIn: null, // user is logged?
      CurrentUser: null // object from server(response)
   });
   return (
      // attribute value sends to the hook in component where it's initialized
      <CurrentUserContext.Provider value={[state, setState]}>
         {children}
      </CurrentUserContext.Provider>
   )
}